--Creacion de tabla empresa como sala de espera
create table empresa(
idEmpresa serial primary key,
ruc varchar(20) unique,
email text unique,
telefono varchar(10),
direccion text,
nombre_empresa text,
contacto text,
fecha_ingreso timestamp default current_timestamp,
ciudad text,
password text,
plan text
);


